package com.citi.retail.optima.etl.cache.model;

/**
 * @author av51190
 *
 */
public class ReportingPeriodVO {

	private Integer rptPrd;
	private Integer rptYr;	
	private Integer rptMonth;
	private Integer rptQtr;
	/**
	 * @return the rptPrd
	 */
	public Integer getRptPrd() {
		return rptPrd;
	}
	/**
	 * @param rptPrd the rptPrd to set
	 */
	public void setRptPrd(Integer rptPrd) {
		this.rptPrd = rptPrd;
	}
	/**
	 * @return the rptYr
	 */
	public Integer getRptYr() {
		return rptYr;
	}
	/**
	 * @param rptYr the rptYr to set
	 */
	public void setRptYr(Integer rptYr) {
		this.rptYr = rptYr;
	}
	/**
	 * @return the rptMonth
	 */
	public Integer getRptMonth() {
		return rptMonth;
	}
	/**
	 * @param rptMonth the rptMonth to set
	 */
	public void setRptMonth(Integer rptMonth) {
		this.rptMonth = rptMonth;
	}
	/**
	 * @return the rptQtr
	 */
	public Integer getRptQtr() {
		return rptQtr;
	}
	/**
	 * @param rptQtr the rptQtr to set
	 */
	public void setRptQtr(Integer rptQtr) {
		this.rptQtr = rptQtr;
	}
	
	

	
}
